(181, 1, '2025-10-03 00:00:44', '2025-10-03 03:00:44', '<!-- wp:html -->\n<script>\n(function() {\n    const applyStyles = () => {\n        const container = document.querySelector(\'.jarallax-container\');\n        if (!container) return;\n\n        const iframe = container.querySelector(\'iframe.jarallax-video\');\n\n        // Estilos contenedor\n        container.style.position = \'fixed\';\n        container.style.top = \'0\';\n        container.style.left = \'0\';\n        container.style.width = \'100vw\';\n        container.style.height = \'100vh\';\n        container.style.zIndex = \'-1\';\n        container.style.overflow = \'hidden\';\n\n        // Estilos iframe\n        if (iframe) {\n            iframe.style.position = \'absolute\';\n            iframe.style.top = \'50%\';\n            iframe.style.left = \'50%\';\n            iframe.style.width = \'auto\';\n            iframe.style.height = \'100%\';\n            iframe.style.minWidth = \'100%\';\n            iframe.style.minHeight = \'100%\';\n            iframe.style.transform = \'translate(-50%, -50%)\';\n            iframe.style.objectFit = \'cover\';\n            iframe.style.pointerEvents = \'none\';\n        }\n    };\n\n    // Observador para detectar cuando AWB inserta el contenedor\n    const observer = new MutationObserver(() => {\n        applyStyles();\n    });\n\n    observer.observe(document.body, { childList: true, subtree: true });\n\n    // También aplica inmediatamente por si ya existe\n    applyStyles();\n})();\n</script>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Home', '', 'inherit', 'closed', 'closed', '', '2-revision-v1', '', '', '2025-10-03 00:00:44', '2025-10-03 03:00:44', '', 2, 'http://localhost/wordpress/?p=181', 0, 'revision', '', 0),
